import React, { useState, useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import api from '../utils/api';
import SearchBar from '../components/SearchBar';
import Card from '../components/Card';

const Search = () => {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const [trips, setTrips] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showFilters, setShowFilters] = useState(false);
  const [sortBy, setSortBy] = useState('date');
  const [filters, setFilters] = useState({
    departure: searchParams.get('departure') || '',
    arrival: searchParams.get('arrival') || '',
    date: searchParams.get('date') || '',
    minPrice: searchParams.get('minPrice') || '',
    maxPrice: searchParams.get('maxPrice') || '',
    seats: searchParams.get('seats') || '',
  });

  useEffect(() => {
    fetchTrips();
  }, [filters, sortBy]);

  const fetchTrips = async () => {
    try {
      setLoading(true);
      const params = {};
      if (filters.departure) params.departure = filters.departure;
      if (filters.arrival) params.arrival = filters.arrival;
      if (filters.date) params.date = filters.date;
      if (filters.minPrice) params.minPrice = filters.minPrice;
      if (filters.maxPrice) params.maxPrice = filters.maxPrice;
      if (filters.seats) params.seats = filters.seats;

      const response = await api.get('/trips', { params });
      let tripsData = response.data.trips || [];
      
      // Sort trips
      tripsData = sortTrips(tripsData, sortBy);
      
      setTrips(tripsData);
    } catch (error) {
      console.error('Error fetching trips:', error);
      setTrips([]);
    } finally {
      setLoading(false);
    }
  };

  const sortTrips = (trips, sort) => {
    const sorted = [...trips];
    switch (sort) {
      case 'price-low':
        return sorted.sort((a, b) => a.price - b.price);
      case 'price-high':
        return sorted.sort((a, b) => b.price - a.price);
      case 'date':
        return sorted.sort((a, b) => new Date(a.date) - new Date(b.date));
      case 'seats':
        return sorted.sort((a, b) => b.availableSeats - a.availableSeats);
      default:
        return sorted;
    }
  };

  const handleFilterChange = (key, value) => {
    setFilters({ ...filters, [key]: value });
  };

  const clearFilters = () => {
    setFilters({
      departure: '',
      arrival: '',
      date: '',
      minPrice: '',
      maxPrice: '',
      seats: '',
    });
  };

  const activeFiltersCount = Object.values(filters).filter(v => v).length;

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Search Bar */}
      <div className="bg-white border-b border-gray-200 sticky top-20 z-40">
        <div className="container-custom py-4">
          <SearchBar className="mb-4" />
          
          {/* Filter Toggle & Sort */}
          <div className="flex items-center justify-between flex-wrap gap-4">
            <button
              onClick={() => setShowFilters(!showFilters)}
              className="flex items-center gap-2 px-4 py-2 border border-gray-300 rounded-full hover:shadow-md transition-all font-medium text-sm"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 4a1 1 0 011-1h16a1 1 0 011 1v2.586a1 1 0 01-.293.707l-6.414 6.414a1 1 0 00-.293.707V17l-4 4v-6.586a1 1 0 00-.293-.707L3.293 7.293A1 1 0 013 6.586V4z" />
              </svg>
              Filtres
              {activeFiltersCount > 0 && (
                <span className="bg-primary text-white rounded-full px-2 py-0.5 text-xs">
                  {activeFiltersCount}
                </span>
              )}
            </button>

            <div className="flex items-center gap-2">
              <span className="text-sm text-gray-600">Trier par:</span>
              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                className="px-4 py-2 border border-gray-300 rounded-full text-sm focus:ring-2 focus:ring-primary focus:border-transparent outline-none"
              >
                <option value="date">Date</option>
                <option value="price-low">Prix croissant</option>
                <option value="price-high">Prix d√©croissant</option>
                <option value="seats">Places disponibles</option>
              </select>
            </div>
          </div>

          {/* Filters Panel */}
          {showFilters && (
            <div className="mt-6 p-6 bg-white rounded-2xl border border-gray-200 shadow-lg animate-fade-in">
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Prix minimum (MAD)
                  </label>
                  <input
                    type="number"
                    placeholder="0"
                    value={filters.minPrice}
                    onChange={(e) => handleFilterChange('minPrice', e.target.value)}
                    className="input-field"
                  />
                </div>
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Prix maximum (MAD)
                  </label>
                  <input
                    type="number"
                    placeholder="1000"
                    value={filters.maxPrice}
                    onChange={(e) => handleFilterChange('maxPrice', e.target.value)}
                    className="input-field"
                  />
                </div>
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Places disponibles
                  </label>
                  <input
                    type="number"
                    placeholder="1"
                    min="1"
                    value={filters.seats}
                    onChange={(e) => handleFilterChange('seats', e.target.value)}
                    className="input-field"
                  />
                </div>
              </div>
              {activeFiltersCount > 0 && (
                <button
                  onClick={clearFilters}
                  className="mt-6 text-sm text-primary hover:underline font-medium"
                >
                  Effacer tous les filtres
                </button>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Results */}
      <div className="container-custom py-8">
        {loading ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {[...Array(8)].map((_, i) => (
              <div key={i} className="bg-white rounded-2xl overflow-hidden shadow-card animate-pulse">
                <div className="w-full h-64 bg-gray-200"></div>
                <div className="p-5">
                  <div className="h-4 bg-gray-200 rounded w-3/4 mb-3"></div>
                  <div className="h-3 bg-gray-200 rounded w-1/2 mb-4"></div>
                  <div className="h-8 bg-gray-200 rounded w-full"></div>
                </div>
              </div>
            ))}
          </div>
        ) : trips.length === 0 ? (
          <div className="text-center py-20">
            <div className="text-6xl mb-4">üîç</div>
            <h3 className="text-2xl font-bold text-gray-900 mb-2">
              Aucun trajet trouv√©
            </h3>
            <p className="text-gray-600 mb-8">
              Essayez de modifier vos crit√®res de recherche
            </p>
            <button
              onClick={clearFilters}
              className="btn-primary"
            >
              Effacer les filtres
            </button>
          </div>
        ) : (
          <>
            <div className="mb-6">
              <h2 className="text-2xl font-bold text-gray-900">
                {trips.length} trajet{trips.length > 1 ? 's' : ''} disponible{trips.length > 1 ? 's' : ''}
              </h2>
              {activeFiltersCount > 0 && (
                <p className="text-gray-600 mt-1">
                  R√©sultats filtr√©s selon vos crit√®res
                </p>
              )}
            </div>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
              {trips.map((trip) => (
                <Card key={trip._id} trip={trip} className="card-hover" />
              ))}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Search;
