import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../utils/api';
import './Home.css';

const Home = () => {
  const [trips, setTrips] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetchTrips();
  }, []);

  const fetchTrips = async () => {
    try {
      setLoading(true);
      const response = await api.get('/trips');
      setTrips(response.data.trips || []);
    } catch (error) {
      console.error('Error fetching trips:', error);
      setTrips([]);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const params = new URLSearchParams();

    if (formData.get('departure')) params.append('departure', formData.get('departure'));
    if (formData.get('arrival')) params.append('arrival', formData.get('arrival'));
    if (formData.get('date')) params.append('date', formData.get('date'));

    navigate(`/search?${params.toString()}`);
  };

  return (
    <div className="home">
      <header className="hero">
        <div className="hero-overlay"></div>
        <div className="hero-content">
          <h1>Voyagez ensemble au Maroc</h1>
          <p>Trouvez des covoiturages pour découvrir le Maroc de manière économique et conviviale.</p>

          <form className="search-box" onSubmit={handleSearch}>
            <div className="input-group">
              <label><i className="fa-solid fa-location-dot"></i> Départ</label>
              <input
                type="text"
                name="departure"
                placeholder="Ville de départ"
              />
            </div>
            <div className="divider"></div>
            <div className="input-group">
              <label><i className="fa-solid fa-location-dot"></i> Arrivée</label>
              <input
                type="text"
                name="arrival"
                placeholder="Ville d'arrivée"
              />
            </div>
            <div className="divider"></div>
            <div className="input-group">
              <label><i className="fa-regular fa-calendar"></i> Date</label>
              <input
                type="date"
                name="date"
                min={new Date().toISOString().split('T')[0]}
              />
            </div>
            <button type="submit" className="search-btn">
              <i className="fa-solid fa-magnifying-glass"></i> Rechercher
            </button>
          </form>
        </div>
      </header>

      <section className="container" id="trips">
        <div className="section-header">
          <h2>Trajets récents</h2>
          <p>Rejoignez des conducteurs pour voyager ensemble.</p>
        </div>



        {loading ? (
          <div className="loading">Chargement...</div>
        ) : trips.length === 0 ? (
          <div className="no-result">
            <h3><i className="fa-solid fa-car-slash"></i> Aucun trajet disponible</h3>
            <p>Soyez le premier à publier un trajet !</p>
          </div>
        ) : (
          <div className="hotel-grid">
            {trips.map((trip) => (
              <div key={trip._id} className="card">
                <div className="card-content">
                  <div className="card-header">
                    <h3>{trip.departure} → {trip.arrival}</h3>
                    <p className="date">{new Date(trip.date).toLocaleDateString('fr-FR')} à {trip.time}</p>
                  </div>
                  <p className="location">
                    <i className="fa-solid fa-user"></i> {trip.driver?.name || 'Conducteur'}
                  </p>
                  <div className="price-row">
                    <div className="price-tag">
                      {trip.price} MAD <span>/ place</span>
                    </div>
                    <div className="rating">
                      <i className="fa-solid fa-users"></i> {trip.availableSeats}/{trip.seats} places
                    </div>
                  </div>
                  <button
                    className="btn-book"
                    onClick={() => navigate(`/trip/${trip._id}`)}
                  >
                    Voir détails
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </section>
    </div>
  );
};

export default Home;
