import React, { useState, useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import api from '../utils/api';
import './Search.css';

const Search = () => {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const [trips, setTrips] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    departure: searchParams.get('departure') || '',
    arrival: searchParams.get('arrival') || '',
    date: searchParams.get('date') || '',
    minPrice: searchParams.get('minPrice') || '',
    maxPrice: searchParams.get('maxPrice') || ''
  });

  useEffect(() => {
    fetchTrips();
  }, [filters]);

  const fetchTrips = async () => {
    try {
      setLoading(true);
      const params = {};
      if (filters.departure) params.departure = filters.departure;
      if (filters.arrival) params.arrival = filters.arrival;
      if (filters.date) params.date = filters.date;
      if (filters.minPrice) params.minPrice = filters.minPrice;
      if (filters.maxPrice) params.maxPrice = filters.maxPrice;

      const response = await api.get('/trips', { params });
      setTrips(response.data.trips || []);
    } catch (error) {
      console.error('Error fetching trips:', error);
      setTrips([]);
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (key, value) => {
    setFilters({ ...filters, [key]: value });
  };

  return (
    <div className="search-page">
      <div className="search-container">
        {/* Sidebar Filters */}
        <aside className="search-sidebar">
          <h3>Filtres</h3>
          
          <div className="filter-section">
            <label>Prix par nuit</label>
            <div className="price-range">
              <input
                type="number"
                placeholder="Min"
                value={filters.minPrice}
                onChange={(e) => handleFilterChange('minPrice', e.target.value)}
              />
              <span>-</span>
              <input
                type="number"
                placeholder="Max"
                value={filters.maxPrice}
                onChange={(e) => handleFilterChange('maxPrice', e.target.value)}
              />
            </div>
          </div>

          <div className="filter-section">
            <label>Catégorie</label>
            <select
              value={filters.category}
              onChange={(e) => handleFilterChange('category', e.target.value)}
            >
              <option value="all">Toutes</option>
              <option value="Marrakech">Marrakech</option>
              <option value="Casablanca">Casablanca</option>
              <option value="Agadir">Agadir</option>
              <option value="Fès">Fès</option>
              <option value="Rabat">Rabat</option>
              <option value="Nord">Nord</option>
              <option value="Désert">Désert</option>
              <option value="Luxe">Luxe</option>
              <option value="Business">Business</option>
              <option value="Economique">Économique</option>
            </select>
          </div>

          <div className="filter-section">
            <label>Nombre de personnes</label>
            <input
              type="number"
              min="1"
              max="10"
              value={filters.guests}
              onChange={(e) => handleFilterChange('guests', e.target.value)}
            />
          </div>
        </aside>

        {/* Results */}
        <div className="search-results">
          {loading ? (
            <div className="loading">Chargement...</div>
          ) : trips.length === 0 ? (
            <div className="no-results">
              <h3>Aucun trajet trouvé</h3>
              <p>Essayez de modifier vos critères de recherche</p>
            </div>
          ) : (
            <>
              <div className="results-header">
                <h2>{trips.length} trajet{trips.length > 1 ? 's' : ''} trouvé{trips.length > 1 ? 's' : ''}</h2>
              </div>
              <div className="hotels-grid">
                {trips.map((trip) => (
                  <div
                    key={trip._id}
                    className="hotel-card"
                    onClick={() => navigate(`/trip/${trip._id}`)}
                  >
                    <div className="hotel-info">
                      <div className="hotel-location">
                        <i className="fa-solid fa-route"></i> {trip.departure} → {trip.arrival}
                      </div>
                      <h3>{new Date(trip.date).toLocaleDateString('fr-FR')} à {trip.time}</h3>
                      <div className="hotel-features">
                        <span>Conducteur: {trip.driver?.name}</span>
                        <span>Véhicule: {trip.vehicle || 'Non spécifié'}</span>
                      </div>
                      <div className="hotel-footer">
                        <div className="hotel-rating">
                          <i className="fa-solid fa-users"></i> {trip.availableSeats}/{trip.seats} places
                        </div>
                        <div className="hotel-price">
                          <strong>{trip.price} MAD</strong>
                          <span>/place</span>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Search;
